import React from "react";
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { Trash2, Eye, Mail, Phone, Calendar, User } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Contact } from "@shared/schema";

export default function AdminContacts() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedContact, setSelectedContact] = useState<Contact | null>(null);
  const [viewDialogOpen, setViewDialogOpen] = useState(false);

  // Explicitly use apiRequest so Authorization headers (admin token) are included.
  const { data: contacts = [], isLoading, isError } = useQuery<Contact[]>({
    queryKey: ["/api/contacts"],
    queryFn: async () => {
      const res = await apiRequest("GET", "/api/contacts");
      return await res.json();
    },
  });

  const deleteContact = useMutation({
    mutationFn: async (id: number) => {
      const res = await apiRequest("DELETE", `/api/contacts/${id}`);
      if (!res.ok) throw new Error('Delete failed');
      return id;
    },
    onMutate: async (deletedId) => {
      await queryClient.cancelQueries({ queryKey: ["/api/contacts"] });
      const previousContacts = queryClient.getQueryData(["/api/contacts"]);
      queryClient.setQueryData(["/api/contacts"], (old: Contact[] = []) => 
        old.filter(item => item.id !== deletedId)
      );
      return { previousContacts };
    },
    onError: (error, deletedId, context) => {
      if (context?.previousContacts) {
        queryClient.setQueryData(["/api/contacts"], context.previousContacts);
      }
      toast({
        title: "Error",
        description: error.message || "Failed to delete message.",
        variant: "destructive",
      });
    },
    onSuccess: () => {
      toast({
        title: "Message deleted",
        description: "The contact message has been removed successfully.",
      });
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
    },
  });

  // Mark contact as read when viewing
  const markRead = useMutation({
    mutationFn: async (id: number) => {
      const res = await apiRequest("PATCH", `/api/contacts/${id}`, { isRead: true });
      if (!res.ok) throw new Error('Failed to mark as read');
      return await res.json();
    },
    onMutate: async (id) => {
      await queryClient.cancelQueries({ queryKey: ["/api/contacts"] });
      const previousContacts = queryClient.getQueryData(["/api/contacts"]);
      queryClient.setQueryData(["/api/contacts"], (old: Contact[] = []) => 
        old.map(contact => contact.id === id ? { ...contact, isRead: true } : contact)
      );
      return { previousContacts };
    },
    onError: (error, id, context) => {
      if (context?.previousContacts) {
        queryClient.setQueryData(["/api/contacts"], context.previousContacts);
      }
      console.error("Failed to mark contact read:", error);
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
    },
  });

  const handleViewMessage = (contact: Contact) => {
    setSelectedContact(contact);
    setViewDialogOpen(true);

    if (!contact.isRead) {
      // fire-and-forget: mark message as read on server and refresh list
      markRead.mutate(contact.id);
    }
  };

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-foreground" data-testid="text-page-title">
          Contact Messages
        </h1>
        <p className="text-muted-foreground mt-2">
          View and manage messages sent through the contact form
        </p>
      </div>

      {isLoading ? (
        <Card>
          <CardContent className="py-12 text-center">
            <p className="text-muted-foreground">Loading messages...</p>
          </CardContent>
        </Card>
      ) : isError ? (
        <Card>
          <CardContent className="py-12 text-center text-red-700">
            Failed to load messages. Check server logs and authentication.
          </CardContent>
        </Card>
      ) : contacts.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <p className="text-muted-foreground">No contact messages yet.</p>
          </CardContent>
        </Card>
      ) : (
        <Card>
          <Table>
            <TableHeader className="bg-primary/10">
              <TableRow>
                <TableHead>Name</TableHead>
                <TableHead>Email</TableHead>
                <TableHead>Subject</TableHead>
                <TableHead>Date</TableHead>
                <TableHead>Status</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {contacts.map((contact) => (
                <TableRow key={contact.id} data-testid={`row-contact-${contact.id}`}>
                  <TableCell className="font-medium">{contact.name}</TableCell>
                  <TableCell>{contact.email}</TableCell>
                  <TableCell className="max-w-md truncate">{contact.subject}</TableCell>
                  <TableCell>
                    {new Date(contact.createdAt).toLocaleDateString()}
                  </TableCell>
                  <TableCell>
                    <Badge
                      variant={contact.isRead ? "secondary" : "default"}
                      className={contact.isRead ? "" : "bg-primary"}
                    >
                      {contact.isRead ? "Read" : "New"}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right">
                    <div className="flex gap-2 justify-end">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleViewMessage(contact)}
                        data-testid={`button-view-${contact.id}`}
                      >
                        <Eye className="w-4 h-4 mr-1" />
                        View
                      </Button>
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={() => deleteContact.mutate(contact.id)}
                        disabled={deleteContact.isPending}
                        data-testid={`button-delete-${contact.id}`}
                      >
                        <Trash2 className="w-4 h-4 mr-1" />
                        Delete
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Card>
      )}

      {/* View Message Dialog */}
      <Dialog open={viewDialogOpen} onOpenChange={setViewDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Contact Message Details</DialogTitle>
          </DialogHeader>

          {selectedContact && (
            <div className="space-y-6 mt-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="flex items-start gap-3">
                  <User className="w-5 h-5 text-muted-foreground mt-1" />
                  <div>
                    <p className="text-sm text-muted-foreground">Name</p>
                    <p className="font-medium" data-testid="text-contact-name">
                      {selectedContact.name}
                    </p>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <Mail className="w-5 h-5 text-muted-foreground mt-1" />
                  <div>
                    <p className="text-sm text-muted-foreground">Email</p>
                    <p className="font-medium break-all" data-testid="text-contact-email">
                      {selectedContact.email}
                    </p>
                  </div>
                </div>

                {selectedContact.phone && (
                  <div className="flex items-start gap-3">
                    <Phone className="w-5 h-5 text-muted-foreground mt-1" />
                    <div>
                      <p className="text-sm text-muted-foreground">Phone</p>
                      <p className="font-medium" data-testid="text-contact-phone">
                        {selectedContact.phone}
                      </p>
                    </div>
                  </div>
                )}

                <div className="flex items-start gap-3">
                  <Calendar className="w-5 h-5 text-muted-foreground mt-1" />
                  <div>
                    <p className="text-sm text-muted-foreground">Date Received</p>
                    <p className="font-medium">
                      {new Date(selectedContact.createdAt).toLocaleString()}
                    </p>
                  </div>
                </div>
              </div>

              <div className="border-t pt-4">
                <p className="text-sm text-muted-foreground mb-2">Subject</p>
                <p className="font-semibold text-lg" data-testid="text-contact-subject">
                  {selectedContact.subject}
                </p>
              </div>

              <div className="border-t pt-4">
                <p className="text-sm text-muted-foreground mb-2">Message</p>
                <div
                  className="bg-muted/30 p-4 rounded-lg min-h-[200px]"
                  data-testid="text-contact-message"
                >
                  <p className="whitespace-pre-wrap">{selectedContact.message}</p>
                </div>
              </div>

              <div className="flex gap-3 pt-4 border-t">
                <Button
                  onClick={() => setViewDialogOpen(false)}
                  className="flex-1"
                  data-testid="button-close-dialog"
                >
                  Close
                </Button>
                <Button
                  variant="destructive"
                  onClick={() => {
                    deleteContact.mutate(selectedContact.id);
                    setViewDialogOpen(false);
                  }}
                  disabled={deleteContact.isPending}
                  data-testid="button-delete-from-dialog"
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Delete Message
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/admin/Affiliations.tsx.content:
import React from "react";
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Plus, Trash, Edit } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { ImageUpload } from "@/components/admin/ImageUpload";
import type { Affiliation } from "@shared/schema";

/**
 * Affiliations Admin Page
 * 
 * CRITICAL FIX: Connected to actual API endpoints
 * Affiliations are now saved to the database and appear on the live site
 */
export default function AdminAffiliations() {
  const queryClient = useQueryClient();

  const { data: affiliations = [] } = useQuery<Affiliation[]>({
    queryKey: ["/api/affiliations"],
  });

  const [open, setOpen] = useState(false);
  const [editingAffiliation, setEditingAffiliation] = useState<Affiliation | null>(null);
  const [form, setForm] = useState({
    name: "",
    logoUrl: "",
    website: "",
    description: "",
    order: 0,
  });

  const saveAffiliation = useMutation({
    mutationFn: async () => {
      const method = editingAffiliation ? "PATCH" : "POST";
      const url = editingAffiliation ? `/api/affiliations/${editingAffiliation.id}` : "/api/affiliations";
      await apiRequest(method, url, form);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/affiliations"] });
      setOpen(false);
      setEditingAffiliation(null);
      setForm({ name: "", logoUrl: "", website: "", description: "", order: 0 });
    },
  });

  const deleteAffiliation = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest("DELETE", `/api/affiliations/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/affiliations"] });
    },
  });

  const handleSave = () => {
    if (!form.name || !form.logoUrl) {
      alert("Name and logo are required!");
      return;
    }
    saveAffiliation.mutate();
  };

  const handleEdit = (affiliation: Affiliation) => {
    setEditingAffiliation(affiliation);
    setForm({
      name: affiliation.name,
      logoUrl: affiliation.logoUrl,
      website: affiliation.website || "",
      description: affiliation.description || "",
      order: affiliation.order,
    });
    setOpen(true);
  };

  const handleDelete = (id: number) => {
    if (window.confirm("Are you sure you want to delete this affiliation?")) {
      deleteAffiliation.mutate(id);
    }
  };

  return (
    <div>
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Affiliations Management</h1>
          <p className="text-muted-foreground mt-2">Manage international affiliations</p>
        </div>
        <Dialog open={open} onOpenChange={setOpen}>
          <DialogTrigger asChild>
            <Button 
              className="bg-primary hover:bg-primary/90" 
              data-testid="button-add-affiliation"
              onClick={() => {
                setEditingAffiliation(null);
                setForm({ name: "", logoUrl: "", website: "", description: "", order: 0 });
              }}
            >
              <Plus className="w-4 h-4 mr-2" />
              Add Affiliation
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>{editingAffiliation ? "Edit Affiliation" : "Add Affiliation"}</DialogTitle>
            </DialogHeader>
            <div className="space-y-4 mt-4">
              <div>
                <Label>Organization Name *</Label>
                <Input 
                  placeholder="IJRU, IRSO, etc." 
                  data-testid="input-name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                />
              </div>
              <ImageUpload
                label="Organization Logo *"
                value={form.logoUrl}
                onChange={(url) => setForm({ ...form, logoUrl: url })}
              />
              <div>
                <Label>Website</Label>
                <Input 
                  placeholder="https://ijru.sport" 
                  data-testid="input-website"
                  value={form.website}
                  onChange={(e) => setForm({ ...form, website: e.target.value })}
                />
              </div>
              <div>
                <Label>Description</Label>
                <Textarea 
                  placeholder="Brief description..." 
                  className="min-h-[100px]" 
                  data-testid="input-description"
                  value={form.description}
                  onChange={(e) => setForm({ ...form, description: e.target.value })}
                />
              </div>
              <div>
                <Label>Display Order</Label>
                <Input 
                  type="number" 
                  placeholder="0" 
                  data-testid="input-order"
                  value={form.order}
                  onChange={(e) => setForm({ ...form, order: Number(e.target.value) })}
                />
              </div>
              <Button 
                className="w-full bg-primary hover:bg-primary/90" 
                data-testid="button-save-affiliation"
                onClick={handleSave}
                disabled={saveAffiliation.isPending}
              >
                {saveAffiliation.isPending ? "Saving..." : editingAffiliation ? "Update Affiliation" : "Save Affiliation"}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {affiliations.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <p className="text-muted-foreground">No affiliations yet. Click "Add Affiliation" to create one.</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {affiliations
            .sort((a, b) => a.order - b.order)
            .map((affiliation) => (
              <Card key={affiliation.id} className="p-4">
                <CardContent>
                  <img 
                    src={affiliation.logoUrl} 
                    alt={affiliation.name}
                    className="w-full h-32 object-contain mb-4"
                  />
                  <h3 className="font-bold text-lg">{affiliation.name}</h3>
                  {affiliation.website && (
                    <a href={affiliation.website} target="_blank" rel="noopener noreferrer" className="text-sm text-blue-600 hover:underline">
                      {affiliation.website}
                    </a>
                  )}
                  {affiliation.description && (
                    <p className="text-sm text-muted-foreground mt-2">{affiliation.description}</p>
                  )}
                  <div className="flex gap-2 mt-4">
                    <Button variant="outline" size="sm" onClick={() => handleEdit(affiliation)}>
                      <Edit className="w-4 h-4 mr-1" /> Edit
                    </Button>
                    <Button variant="destructive" size="sm" onClick={() => handleDelete(affiliation.id)}>
                      <Trash className="w-4 h-4 mr-1" /> Delete
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
        </div>
      )}
    </div>
  );
}

