import React from "react";
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { apiRequest } from "@/lib/queryClient";

export default function AdminSettings() {
  const API_URL = "/api/site-settings";

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [settings, setSettings] = useState({
    siteTitle: "Nigeria Rope Skipping Association",
    phone: "+2347069465965",
    email: "info@nrsa.ng",
    about: "",
    facebook: "",
    twitter: "",
    instagram: "",
  });

  // Fetch settings on load
  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    setLoading(true);
    try {
      const res = await apiRequest("GET", API_URL);
      const data = await res.json();
      setSettings(data);
    } catch (err) {
      console.error("Error loading settings:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setSettings({ ...settings, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      await apiRequest("POST", API_URL, settings);
      alert("âœ… Settings updated successfully!");
    } catch (err) {
      console.error("Error saving settings:", err);
      alert("âŒ Failed to save settings.");
    } finally {
      setSaving(false);
    }
  };

  if (loading)
    return (
      <p className="text-center text-muted-foreground py-12">
        Loading site settings...
      </p>
    );

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-foreground">Site Settings</h1>
        <p className="text-muted-foreground mt-2">
          Manage website configuration
        </p>
      </div>

      <div className="space-y-6">
        {/* General Settings */}
        <Card>
          <CardHeader>
            <CardTitle>General Settings</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label>Site Title</Label>
              <Input
                name="siteTitle"
                value={settings.siteTitle}
                onChange={handleChange}
              />
            </div>
            <div>
              <Label>Contact Phone</Label>
              <Input name="phone" value={settings.phone} onChange={handleChange} />
            </div>
            <div>
              <Label>Contact Email</Label>
              <Input name="email" value={settings.email} onChange={handleChange} />
            </div>
            <div>
              <Label>About Text (Homepage)</Label>
              <Textarea
                name="about"
                className="min-h-[120px]"
                value={settings.about}
                onChange={handleChange}
              />
            </div>
          </CardContent>
        </Card>

        {/* Social Media */}
        <Card>
          <CardHeader>
            <CardTitle>Social Media Links</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label>Facebook URL</Label>
              <Input
                name="facebook"
                placeholder="https://facebook.com/nrsa"
                value={settings.facebook}
                onChange={handleChange}
              />
            </div>
            <div>
              <Label>Twitter URL</Label>
              <Input
                name="twitter"
                placeholder="https://twitter.com/nrsa"
                value={settings.twitter}
                onChange={handleChange}
              />
            </div>
            <div>
              <Label>Instagram URL</Label>
              <Input
                name="instagram"
                placeholder="https://instagram.com/nrsa"
                value={settings.instagram}
                onChange={handleChange}
              />
            </div>
          </CardContent>
        </Card>

        <Button
          onClick={handleSave}
          disabled={saving}
          className="bg-primary hover:bg-primary/90"
        >
          {saving ? "Saving..." : "Save Settings"}
        </Button>
      </div>
    </div>
  );
}

client/src/pages/admin/News.tsx.content:
import React from "react";
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Plus, Edit, Trash } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Switch } from "@/components/ui/switch";
import { ImageUpload } from "@/components/admin/ImageUpload";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

type News = {
  id: number;
  title: string;
  excerpt: string;
  content: string;
  imageUrl?: string;
  isFeatured?: boolean;
  publishedAt: string;
};

export default function AdminNews() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { data: newsItems = [] } = useQuery<News[]>({
    queryKey: ["/api/news"],
  });

  const [dialogOpen, setDialogOpen] = useState(false);
  const [editItem, setEditItem] = useState<News | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    excerpt: "",
    content: "",
    imageUrl: "",
    isFeatured: false,
  });

  // ðŸ”¹ Create or Update
  const saveMutation = useMutation({
    mutationFn: async () => {
      const method = editItem ? "PATCH" : "POST";
      const url = editItem ? `/api/news/${editItem.id}` : "/api/news";

      const res = await apiRequest(method, url, formData);
      if (!res.ok) throw new Error('Save failed');
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/news"] });
      toast({
        title: editItem ? "Article Updated" : "Article Created",
        description: "News article saved successfully!",
      });
      setDialogOpen(false);
      setEditItem(null);
      resetForm();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save article.",
        variant: "destructive",
      });
    },
  });

  // ðŸ”¹ Delete
  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      const res = await apiRequest("DELETE", `/api/news/${id}`);
      if (!res.ok) throw new Error('Delete failed');
      return id;
    },
    onMutate: async (deletedId) => {
      await queryClient.cancelQueries({ queryKey: ["/api/news"] });
      const previousNews = queryClient.getQueryData(["/api/news"]);
      queryClient.setQueryData(["/api/news"], (old: News[] = []) => 
        old.filter(item => item.id !== deletedId)
      );
      return { previousNews };
    },
    onError: (error, deletedId, context) => {
      queryClient.setQueryData(["/api/news"], context?.previousNews);
      toast({
        title: "Error",
        description: error.message || "Failed to delete article.",
        variant: "destructive",
      });
    },
    onSuccess: () => {
      toast({
        title: "Article Deleted",
        description: "News article removed successfully.",
      });
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/news"] });
    },
  });

  const handleSave = () => {
    if (!formData.title || !formData.excerpt || !formData.content) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields.",
        variant: "destructive",
      });
      return;
    }
    saveMutation.mutate();
  };

  const handleEdit = (item: News) => {
    setEditItem(item);
    setFormData({
      title: item.title,
      excerpt: item.excerpt,
      content: item.content,
      imageUrl: item.imageUrl || "",
      isFeatured: item.isFeatured || false,
    });
    setDialogOpen(true);
  };

  const handleDelete = (id: number) => {
    if (window.confirm("Are you sure you want to delete this article?")) {
      deleteMutation.mutate(id);
    }
  };

  const resetForm = () =>
    setFormData({
      title: "",
      excerpt: "",
      content: "",
      imageUrl: "",
      isFeatured: false,
    });

  return (
    <div>
      {/* Header */}
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-foreground">News Management</h1>
          <p className="text-muted-foreground mt-2">Create and manage news articles</p>
        </div>
        <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
          <DialogTrigger asChild>
            <Button
              className="bg-primary hover:bg-primary/90"
              onClick={() => {
                setEditItem(null);
                resetForm();
              }}
            >
              <Plus className="w-4 h-4 mr-2" />
              Add News
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>{editItem ? "Edit News Article" : "Add News Article"}</DialogTitle>
            </DialogHeader>
            <div className="space-y-4 mt-4">
              <div>
                <Label>Title *</Label>
                <Input
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  placeholder="Article title"
                />
              </div>
              <div>
                <Label>Excerpt *</Label>
                <Textarea
                  value={formData.excerpt}
                  onChange={(e) => setFormData({ ...formData, excerpt: e.target.value })}
                  placeholder="Brief summary..."
                  className="min-h-[80px]"
                />
              </div>
              <div>
                <Label>Content *</Label>
                <Textarea
                  value={formData.content}
                  onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                  placeholder="Full article content..."
                  className="min-h-[200px]"
                />
              </div>
              <ImageUpload
                label="Article Image"
                value={formData.imageUrl}
                onChange={(url) => setFormData({ ...formData, imageUrl: url })}
              />
              <div className="flex items-center gap-2">
                <Switch
                  checked={formData.isFeatured}
                  onCheckedChange={(checked) => setFormData({ ...formData, isFeatured: checked })}
                />
                <Label>Featured Article</Label>
              </div>
              <Button 
                className="w-full bg-primary hover:bg-primary/90" 
                onClick={handleSave}
                disabled={saveMutation.isPending}
              >
                {saveMutation.isPending ? "Saving..." : editItem ? "Update Article" : "Save Article"}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* News Table */}
      {newsItems.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <p className="text-muted-foreground">
              No news articles yet. Click "Add News" to create one.
            </p>
          </CardContent>
        </Card>
      ) : (
        <Card>
          <Table>
            <TableHeader className="bg-primary/10">
              <TableRow>
                <TableHead>Title</TableHead>
                <TableHead>Published</TableHead>
                <TableHead>Featured</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {newsItems.map((item) => (
                <TableRow key={item.id}>
                  <TableCell>{item.title}</TableCell>
                  <TableCell>{new Date(item.publishedAt).toLocaleDateString()}</TableCell>
                  <TableCell>{item.isFeatured ? "Yes" : "No"}</TableCell>
                  <TableCell className="text-right space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(item)}
                      className="hover:bg-primary/10"
                    >
                      <Edit className="w-4 h-4 mr-1" /> Edit
                    </Button>
                    <Button
                      variant="destructive"
                      size="sm"
                      onClick={() => handleDelete(item.id)}
                      disabled={deleteMutation.isPending}
                    >
                      <Trash className="w-4 h-4 mr-1" /> {deleteMutation.isPending ? "Deleting..." : "Delete"}
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Card>
      )}
    </div>
  );
}

