import React from "react";
import { useState, useRef } from "react";
import { Upload, X, ImageIcon } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

interface ImageUploadProps {
  value: string;
  onChange: (url: string) => void;
  label?: string;
}

/**
 * ImageUpload Component
 * 
 * A drag-and-drop image upload component for the admin dashboard.
 * 
 * Features:
 * - Drag and drop file upload
 * - Click to browse file selection
 * - Image preview
 * - Option to use URL instead of upload
 * - File validation (images only, max 5MB)
 * - Progress indication during upload
 * 
 * Usage:
 * <ImageUpload 
 *   value={imageUrl} 
 *   onChange={(url) => setImageUrl(url)}
 *   label="Hero Image"
 * />
 * 
 * Security:
 * - Requires admin JWT token (automatically included by apiRequest)
 * - Server-side validation of file type and size
 * - Only authenticated admins can upload files
 */
export function ImageUpload({ value, onChange, label = "Image" }: ImageUploadProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadError, setUploadError] = useState("");
  const [useUrl, setUseUrl] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  /**
   * Handle file upload to server
   * Sends file to /api/upload endpoint with admin authentication
   */
  const handleFileUpload = async (file: File) => {
    // Validate file type
    if (!file.type.startsWith("image/")) {
      setUploadError("Please upload an image file");
      return;
    }

    // Validate file size (5MB)
    if (file.size > 5 * 1024 * 1024) {
      setUploadError("File size must be less than 5MB");
      return;
    }

    setIsUploading(true);
    setUploadError("");

    try {
      // Get admin token from localStorage
      const token = localStorage.getItem("adminToken");
      if (!token) {
        throw new Error("Not authenticated");
      }

      // Create form data
      const formData = new FormData();
      formData.append("file", file);

      // Upload to server
      const response = await fetch("/api/upload", {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${token}`
        },
        body: formData
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Upload failed");
      }

      const data = await response.json();
      onChange(data.url); // Update parent component with new URL
    } catch (error: any) {
      setUploadError(error.message || "Upload failed");
    } finally {
      setIsUploading(false);
    }
  };

  // Handle drag events
  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);

    const file = e.dataTransfer.files[0];
    if (file) {
      handleFileUpload(file);
    }
  };

  // Handle file selection via input
  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      handleFileUpload(file);
    }
  };

  // Trigger file input click
  const handleBrowseClick = () => {
    fileInputRef.current?.click();
  };

  // Clear uploaded image
  const handleClear = () => {
    onChange("");
    setUploadError("");
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  return (
    <div className="space-y-2">
      <div className="flex items-center justify-between">
        <Label>{label}</Label>
        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => setUseUrl(!useUrl)}
          className="text-xs"
        >
          {useUrl ? "Use Upload" : "Use URL Instead"}
        </Button>
      </div>

      {useUrl ? (
        // URL Input Mode
        <Input
          type="url"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          placeholder="https://example.com/image.jpg"
        />
      ) : (
        // Upload Mode
        <>
          {!value ? (
            // Upload Area
            <div
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
              className={`
                border-2 border-dashed rounded-lg p-8 text-center cursor-pointer
                transition-colors
                ${isDragging ? "border-primary bg-primary/5" : "border-gray-300 hover:border-primary/50"}
                ${isUploading ? "opacity-50 pointer-events-none" : ""}
              `}
              onClick={handleBrowseClick}
            >
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={handleFileSelect}
                className="hidden"
              />

              <div className="flex flex-col items-center gap-2">
                <Upload className={`w-12 h-12 ${isDragging ? "text-primary" : "text-gray-400"}`} />
                <div>
                  <p className="font-medium">
                    {isUploading ? "Uploading..." : "Drop image here or click to browse"}
                  </p>
                  <p className="text-sm text-muted-foreground mt-1">
                    PNG, JPG, GIF or WebP (max 5MB)
                  </p>
                </div>
              </div>
            </div>
          ) : (
            // Image Preview
            <div className="relative border rounded-lg overflow-hidden">
              <img
                src={value}
                alt="Preview"
                className="w-full h-48 object-cover"
              />
              <Button
                type="button"
                variant="destructive"
                size="icon"
                className="absolute top-2 right-2"
                onClick={handleClear}
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          )}

          {uploadError && (
            <p className="text-sm text-destructive">{uploadError}</p>
          )}
        </>
      )}
    </div>
  );
}

server/upload.ts.content:
import express, { Express, Request, Response } from "express";
import multer from "multer";
import { supabase } from "./lib/supabase.js";
import { requireAdmin } from "./authMiddleware.js";

const router = express.Router();
const upload = multer({
  storage: multer.memoryStorage(),
  limits: { fileSize: 10 * 1024 * 1024 }, // 10 MB
  fileFilter: (_req, file, cb) => {
    const allowed = ["image/jpeg", "image/png", "image/gif", "image/webp"];
    if (allowed.includes(file.mimetype)) cb(null, true);
    else cb(new Error("Invalid file type. Only images are allowed."));
  },
});

router.post("/upload", requireAdmin, upload.single("file"), async (req: Request, res: Response) => {
  try {
    // âœ… Handle YouTube or external link uploads
    if (req.body.url) {
      const url = req.body.url.trim();
      let thumbnail = null;

      // If YouTube link, extract thumbnail
      const ytMatch = url.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/)([\w-]+)/);
      if (ytMatch) {
        const videoId = ytMatch[1];
        thumbnail = `https://img.youtube.com/vi/${videoId}/hqdefault.jpg`;
      }

      return res.status(200).json({
        type: ytMatch ? "youtube" : "external",
        url,
        thumbnail,
        message: "External media link stored successfully",
      });
    }

    // âœ… Handle file uploads
    const file = req.file;
    if (!file) return res.status(400).json({ error: "No file uploaded" });

    if (!supabase) {
      return res.status(503).json({ 
        error: "File upload service not configured. Please set SUPABASE_URL and SUPABASE_SERVICE_ROLE_KEY environment variables." 
      });
    }

    const filename = `${Date.now()}-${file.originalname.replace(/\s+/g, "-")}`;
    const filepath = `uploads/${filename}`;

    const { data, error } = await supabase.storage
      .from("nrsa-uploads")
      .upload(filepath, file.buffer, {
        contentType: file.mimetype,
        cacheControl: "3600",
        upsert: false,
      });

    if (error) {
      console.error("Supabase storage error:", error);
      return res.status(500).json({ error: error.message });
    }

    const { data: publicData } = supabase.storage
      .from("nrsa-uploads")
      .getPublicUrl(data.path);

    res.status(200).json({
      type: "image",
      url: publicData.publicUrl,
      message: "File uploaded successfully",
    });
  } catch (error: any) {
    console.error("Upload error:", error);
    res.status(500).json({ error: error.message || "Upload failed" });
  }
});

export function registerUploadRoutes(app: Express) {
  app.use("/api", router);
}

